                <div class="content-order__item col-12 col-md-4">
                    <textarea name="" id="" class="content-order__textarea" placeholder="Комментарий"></textarea>
                </div>
                <div class="content-order__item col-12">
                    <div class="content-order__row row">
                        <div class="content-order__item col-12 col-md-6">
                            <select class="content-order__list-select">
                                <option value="">Все параметры</option>
                            </select>
                        </div>
                        <div class="content-order__send col-12 col-md-6">
                            <input  type="submit" class="content-order__list-submit" value="Оставить заявку">
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</section>